/* Reset & standardize default styles */
@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

/* Design tokens (apply to components) */
:root {
  /* Custom theme options */
  --color-accent: #121212;

  /* Base values */
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2);
  --border-radius: 8px;
  --border-color: #cbcace;
}

/* Root element (use instead of `body`) */
#page {
  font-family: system-ui, sans-serif;
  color: #e6e6e6;
  line-height: 1.2;
  font-size: 1.125rem;
  background: #35d994;
}

/* Elements */
.section-container {
  max-width: 900px;
  margin: 0 auto;
  padding: 5rem 2rem;
}

a.link {
  line-height: 1.3;
  font-weight: 500;
  border-bottom: 2px solid;
  transform: translateY(-2px); /* move link back into place */
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: transparent;
  }
}

.heading {
  font-size: 2.5rem;
  line-height: 1.15;
  font-weight: 700;
}

.button {
  color: white;
  background: var(--color-accent, rebeccapurple);
  border-radius: 5px;
  padding: 8px 20px;
  transition: var(--transition, 0.1s box-shadow);
  border: 0; /* reset */

  &:hover {
    box-shadow: 0 0 0 2px var(--color-accent, rebeccapurple);
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent, rebeccapurple);
  }
}

/* Content Section */
.section .content {
  p {
    padding: 0.25rem 0;
    line-height: 1.5;
  }

  img {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  a.link {
  }

  h1 {
    font-size: 3rem;
    font-weight: 700;
    margin-bottom: 1rem;
  }

  h2 {
    font-size: 2.25rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
  }

  h3 {
    font-size: 1.75rem;
    font-weight: 600;
    margin-bottom: 0.25rem;
  }

  ul {
    list-style: disc;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  ol {
    list-style: decimal;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  blockquote {
    padding: 2rem;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }
}

.section-container { /* Defined in Site CSS */
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 1.5rem;
  place-items: center;
  text-align: center;
  height:80vh;
}

.heading { /* Defined in Site CSS */
  font-size: 3rem;
  font-weight: 800;

  @media (min-width: 700px) {
    font-size: 4rem;
  }
}

.subheading {
  font-size: 1.25rem;
}

.buttons {
  margin-top: 0.5rem;
  display: flex;
  flex-wrap: wrap;
  gap: 1rem;

  .button {} /* Defined in Site CSS */

  .button:not(:only-child):last-child {
    background: inherit;
    border: 2px solid var(--color-accent, rebeccapurple);
    color: var(--color, #222);
  }
}

.section-container {} /* Defined in Site CSS */

.teasers {
  display: grid;
  gap: 6rem;
}

.teaser {
  display: grid;
  gap: 3rem;

  img {
    width: 100%;
    border-radius: var(--border-radius);
  }

  .content { /* Defined in Site CSS */
    display: grid;
    place-items: flex-start;
    gap: 1.5rem;   
    padding: 0;

    .heading {} /* Defined in Site CSS */

    a.link {} /* Defined in Site CSS */
  }

  @media (min-width: 800px) {
    grid-template-columns: 1fr 1fr;
    place-items: center;

    /* switch direction for even items */
    &:nth-child(even) .content {
      grid-row: 1;
      grid-column: 2;
    }

    &:nth-child(even) img {
      grid-row: 1;
      grid-column: 1;
    }

  }
}

@media (min-width: 1200px) {

  section {
    gap: 6rem;
  }

  .teaser {
    gap: 6rem;
  }
}

.section-container { /* Defined in Site CSS */
  display: grid;
  justify-content: center;
}

.card {
  max-width: 640px;
  border: 1px solid var(--border-color, #eee);
  border-radius: var(--border-radius);
  padding: 3.5rem 2.75rem;
  text-align: center;
  display: grid;
  place-items: center;
  gap: 2rem;

  @media (min-width: 600px) {
    padding: 4.5rem 6rem;
  }
}

.heading {} /* Defined in Site CSS */

.link { /* Defined in Site CSS */
  font-weight: 500;
  border-color: transparent;
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: initial;
  }
}

ul {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 1.5rem;

  li {
    color: white;  
    background: var(--color-accent, rebeccapurple); 
    font-size: 1.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    transition: 0.1s transform;

    &:hover {
      transform: scale(1.1);
    }

    a {
      font-size: 1.5rem;
      padding: 0.75rem;
    }
  }
}

.section-container { /* Defined in Site CSS */
  display: grid;
  gap: 2rem;

  @media (min-width: 800px) {
    grid-template-columns: 3fr 2fr;
    gap: 14rem;
  }
}

.content {
  display: grid;
  place-content: flex-start;
  gap: 1rem;

  .social-links {
    display: grid;
    gap: 0.5rem;

    a {
      display: flex;
      align-items: center;
      gap: 0.75rem;

      .icon {
        font-size: 1.25rem;
        color: var(--color-accent, rebeccapurple);
        transition: var(--transition, 0.1s transform);
      }

      &:hover .icon {
        transform: scale(1.1);
      }
    }
  }
}

form {
  display: grid;
  gap: 1rem;
  padding: 2rem;
  border: 1px solid var(--border-color, #eee);
  border-radius: var(--border-radius);

  label {
    display: grid;
    gap: 0.25rem;

    span {
      font-size: 0.75rem;
      font-weight: 500;
    }

    input,
    textarea {
      padding: 0.5rem;
      outline-color: transparent;
      transition: var(--transition, 0.1s outline-color);
      border: 1.5px solid var(--border-color, #eee);
      border-radius: var(--border-radius);

      &:focus {
        outline-color: var(--color-accent, rebeccapurple);
      }
    }
  }

  .button { /* Defined in Site CSS */
    margin-top: 1rem;
  }
}

.section-container {} /* Defined in Site CSS */

.heading { /* Defined in Site CSS */
  text-align: center;
  margin-bottom: 3rem;
}

ul.cards {
  display: grid;
  place-items: center;
  gap: 2rem; 

  li {
    max-width: 400px;
    padding: 2rem;
    border: 1px solid var(--border-color, #eee);
    border-radius: var(--border-radius);

    figure {
      padding: 0 2rem;

      img {
        aspect-ratio: 1 / 1;
        border-radius: 50%;
        object-fit: cover;
        margin-bottom: 2rem;
        width: 100%; /* For Safari */ 
      }
    }

    .info {

      .details {
        display: grid;
        text-align: center;
        margin-bottom: 1rem;

        span.name {
          font-weight: 600;
        }
      }

      .social {
        display: flex;
        justify-content: center;
        gap: 1.5rem;
        font-size: 1.5rem;
        color: var(--color-accent, rebeccapurple);

        a {
          transition: var(--transition, 0.1s transform);

          &:hover {
            transform: scale(1.1);
          }
        }
      }
    }
  }

  @media (min-width: 600px) {
    grid-template-columns: 1fr 1fr;
  }

  @media (min-width: 1000px) {
    grid-template-columns: 1fr 1fr 1fr;
  }
}

.section-container { /* Defined in Site CSS */
  display: flex;
  flex-direction: column;
  gap: 2rem;
}

.heading { /* Defined in Site CSS */
  text-align: center;
}

ul {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(15rem, 1fr));
  gap: 1.5rem;

  li {
    display: flex;
    flex-direction: column;
    gap: 0.5rem;
    border: 1px solid var(--border-color, #eee);
    border-radius: var(--border-radius);
    padding: 1.5rem;

    .title {
      display: flex;
      gap: 0.5rem;
      font-weight: bold;
    }
  }
}

.section-container {
  /* Defined in Site CSS */
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding-top: 1rem;
  padding-bottom: 1rem;
}

.logo {
  color: var(--color-accent);
  font-size: 1.5rem;
  font-weight: 600;
  line-height: 1.2;
  font-weight: bold;
  font-family: system-ui;
}

.desktop-nav {
  display: none;
  align-items: center;
  justify-content: space-between;
  width: 100%;

  nav {
    display: flex;
    gap: 2rem;

    a.link {
      /* Defined in Site CSS */
      border-color: transparent;
      transition: border-color 0.1s;
      font-size: 0.95rem;

      &:hover {
        border-color: initial;
      }
    }
  }

  @media (min-width: 800px) {
    display: flex;
  }
}

.mobile-nav {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  font-weight: 500;

  @media (min-width: 800px) {
    display: none;
  }
}

/* Mobile navigation popup */
#popup {
  font-size: 1.25rem;
  display: grid;
  gap: 1rem;
  position: absolute;
  text-align: center;
  background-color: var(--background-color, white);
  box-shadow: var(--box-shadow, 0px 4px 30px rgba(0, 0, 0, 0.1));
  border-radius: var(--border-radius);
  inset: 0.5rem;
  bottom: initial;
  padding: 2rem;
  z-index: 1;

  button#close {
    position: absolute;
    right: 1rem;
    top: 1rem;
  }
}

